import React, { Component } from 'react'
import axios from 'axios'
import './components.css'
import { Redirect } from 'react-router';
 class contact extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            name: '',
            email: '',
            phone: '',
            college: '',
            gender: '',
            dob: Date.now,
            city: '',
            redirect: false
        }
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handlePhoneChange = this.handlePhoneChange.bind(this);
        this.handleCollegeChange = this.handleCollegeChange.bind(this);
        this.handleGenderChange = this.handlegenderChange.bind(this);
        this.handledobChange = this.handledobChange.bind(this);
        this.handleCityChange = this.handleCityChange.bind(this);
        this.addUser = this.addUser.bind(this);
      }

      handleNameChange(e) {
        this.setState({name: e.target.value})
      }

      handleEmailChange(e) {
        this.setState({email: e.target.value})
      }

      handlePhoneChange(e) {
        this.setState({phone: e.target.value})
      }

      handleCollegeChange(e) {
        this.setState({college: e.target.value})
      }

      handlegenderChange(e) {
        this.setState({gender: e.target.value})
      }

      handledobChange(e) {
        this.setState({dob: e.target.value})
      }

      handleCityChange(e) {
        this.setState({city: e.target.value})
      }

      addUser(event) {
        event.preventDefault();
        const userAdd = {
            name: this.state.name,
            email: this.state.email,
            phone: this.state.phone,
            college: this.state.college,
            gender: this.state.gender,
            dob: this.state.dob,   
            city: this.state.city        
        }
        console.log(userAdd)
        axios.post('http://localhost:4000/contact', userAdd)
        .then(res => { 
            console.log(res);
            this.setState({ redirect: this.state.redirect === false });
        })
        .catch(err => { console.log(err) });
      }
    render() {
        return (
            <div>
                contact page
                <form onSubmit={this.addUser} method="user">
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">Name</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handleNameChange} name="name" value={this.state.name}/>
                    </div>
                </div>
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">Email</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handleEmailChange} name="email" value={this.state.email}/>
                    </div>
                </div>
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">Phone</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handlePhoneChange} name="phone" value={this.state.phone}/>
                    </div>
                </div>
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">College</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handleCollegeChange} name="college" value={this.state.college}/>
                    </div>
                </div>
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">Gender</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handleGenderChange} name="gender" value={this.state.gender}/>
                    </div>
                </div>
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">Date of Birth</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handledobChange} name="dob" value={this.state.dob}/>
                    </div>
                </div>
                <div className="form-group row">
                    <label className="col-sm-2 col-form-label text-left">City</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" onChange={this.handleCityChange} name="city" value={this.state.city}/>
                    </div>
                </div>
                <hr/>
                <div style={{marginLeft: "0px"}} className="row">
                    <button type="submit" onClick={this.userAdd} className="btn btn-warning" style={{marginLeft: "0px"}}>Add User</button>
                </div>
            </form>
            {this.state.redirect && (
                <Redirect to={'/home'}/>
            )}


            </div>
        );
    }
}



export default contact;
